frappe.ui.form.on('Delivery Note', {
	company:function(frm) {
	    frappe.call({
            method: 'frappe.client.get_value',
            args: {
                doctype: 'Company Settings',
                fieldname: 'vietnam_company_name'
            },
            callback: function(response) {
                if (response.message) {
                    var vietnam_company_name = response.message.vietnam_company_name;
            		if (frm.doc.company != vietnam_company_name){
            		    frm.set_value('naming_series', 'MAT-DN-.YYYY.-');

            		}
            		if (frm.doc.company == vietnam_company_name){
            		    frm.set_value('naming_series', '.{custom_abbr}.-DEL-.{custom_id_year}.-.{custom_id_month}.-.####');

            		}
                }
            }
        });
	},

})

frappe.ui.form.on('BOM Creator', {
    before_save: function(frm) {
        if (frm._image_paths_set) return;

        let promises = [];

        (frm.doc.items || []).forEach(row => {
            if (!row.custom_attach) {
                let p = frappe.call({
                    method: "baller_headwear.baller_headwear.api.get_item_image", 
                    args: { item_code: row.item_code },
                    callback: function(r) {
                        if (r.message) {
                            frappe.model.set_value(row.doctype, row.name, 'custom_attach', r.message);
                            console.log(`Set image path for ${row.item_code}: ${r.message}`);
                        } else {
                            console.log(`No image found for ${row.item_code}`);
                        }
                    }
                });
                promises.push(p);
            }
        });

        if (promises.length) {
            frappe.validated = false;  
            Promise.all(promises.map(p => p.then?.() || Promise.resolve())).then(() => {
                frm._image_paths_set = true;
                frappe.show_alert({
                    message: __('Image paths updated. Please click Save again.'),
                    indicator: 'green'
                });
            });
        }
    }
});

