frappe.ui.form.on('Work Order', {
    refresh: function (frm) {
        if (frm.doc.docstatus === 1) {
            frm.add_custom_button(__('Create Material Request'), function () {
                frappe.db.get_doc('Work Order Customization Settings')
                    .then(settings => {
                        frappe.new_doc('Material Request', {
                            work_order: frm.doc.name,
                            company: frm.doc.company,
                            material_request_type: 'Material Transfer',
                            set_from_warehouse: settings.source_warehouse,
                            set_warehouse: settings.target_warehouse
                        });
                    })
                    .catch(err => {
                        frappe.msgprint(__('Failed to fetch Work Order Customization Settings'));
                        console.error(err);
                    });
            }, __('Actions'));
        }
    }
});

frappe.ui.form.on('Material Request', {
    refresh: function(frm) {
        if (frm.doc.docstatus === 1) {
            frm.add_custom_button(__('Material Transfer for Manufacture'), function() {
                frappe.model.open_mapped_doc({
                    method: "baller_headwear.baller_headwear.api.make_stock_entry",
                    frm: frm,
                    args: {
                        stock_entry_type: "Material Transfer for Manufacture"
                    }
                });
            }, __("Create"));
        }
    }
});

