frappe.ui.form.on('Sales Invoice', {     
    custom_transaction_date_exchange_rate: function(frm) {         
        console_log_sales_order_totals(frm);     
    } 
});

async function console_log_sales_order_totals(frm) {     
    if (!frm.doc.items || frm.doc.items.every(item => !item.item_code)) {
        frappe.throw(__('Please add at least one item to the Sales Invoice before proceeding.'));
    }

    let sales_order_totals = {};     
    let sales_order_ids = [];      

    frm.doc.items.forEach(function(item) {         
        if (item.sales_order) {             
            if (sales_order_totals[item.sales_order]) {                 
                sales_order_totals[item.sales_order] += item.amount;             
            } else {                 
                sales_order_totals[item.sales_order] = item.amount;                 
                sales_order_ids.push(item.sales_order);             
            }         
        }     
    });      

    let sales_order_list = [];     
    for (let sales_order_id in sales_order_totals) {         
        sales_order_list.push({             
            sales_order_id: sales_order_id,             
            outstanding_amount: sales_order_totals[sales_order_id],             
            exchange_rate: 1,              
            allocated_amount: 0,             
            payment_entry_id: null         
        });     
    }      

    console.log('Sales Orders and Outstanding Amounts:', sales_order_list);     
    console.log('Unique Sales Order IDs:', sales_order_ids);      

    let updated_sales_order_list = await reduce_advance_amounts(frm, sales_order_list);     
    console.log('Updated Sales Orders after reducing advance amounts:', updated_sales_order_list);      

    let final_calculation = calculate_final_value(frm, updated_sales_order_list);     
    console.log('Final Calculation:', final_calculation);      

    frm.set_value('conversion_rate', final_calculation);     
    frm.save();     
    frappe.msgprint(__('Conversion Rate updated to: ' + final_calculation)); 
}  

function reduce_advance_amounts(frm, sales_order_list) {     
    let promises = [];     
    let sales_order_ids_with_payment = [];     

    frm.doc.advances.forEach(function(advance) {         
        if (advance.reference_name) {             
            let promise = new Promise((resolve, reject) => {                 
                frappe.call({                     
                    method: 'frappe.client.get',                     
                    args: {                         
                        doctype: 'Payment Entry',                         
                        name: advance.reference_name                     
                    },                     
                    callback: function(response) {                         
                        if (response.message) {                             
                            let payment_entry_doc = response.message;                             
                            let source_exchange_rate = payment_entry_doc.source_exchange_rate || 1;                             
                            let payment_entry_id = payment_entry_doc.name;                             

                            payment_entry_doc.references.forEach(function(reference) {                                 
                                let reference_sales_order_id = reference.reference_name;                                 

                                sales_order_ids_with_payment.push(reference_sales_order_id);

                                sales_order_list.forEach(function(order) {                                     
                                    if (order.sales_order_id === reference_sales_order_id) {                                         
                                        let reduced_amount = order.outstanding_amount - advance.allocated_amount;                                         
                                        order.outstanding_amount = reduced_amount < 0 ? 0 : reduced_amount;                                         
                                        order.exchange_rate = source_exchange_rate;                                         
                                        order.allocated_amount += advance.allocated_amount;                                         
                                        order.payment_entry_id = payment_entry_id;                                     
                                    }                                 
                                });                             
                            });                             
                            resolve();                           
                        } else {                             
                            console.error('Failed to fetch Payment Entry:', advance.reference_name);                             
                            reject();                         
                        }                     
                    }                 
                });             
            });             
            promises.push(promise);         
        }     
    });      

    return Promise.all(promises).then(() => {         
        // sales_order_list.forEach(function(order) {             
        //     if (!sales_order_ids_with_payment.includes(order.sales_order_id)) {                 
        //         frappe.throw(__('No Payment Entry found for Sales Order: ' + order.sales_order_id));             
        //     }         
        // });         

        return sales_order_list;     
    }); 
}  

function calculate_final_value(frm, sales_order_list) {     
    let custom_transaction_exchange_rate = frm.doc.custom_transaction_date_exchange_rate || 1;     
    console.log('Custom Exchange Rate:', custom_transaction_exchange_rate);      

    let total_amount = frm.doc.total || 1;      

    console.log('Total Amount:', total_amount);      

    let total_outstanding_amount = 0;     
    let total_allocated_amount = 0;      

    sales_order_list.forEach(function(order) {         
        total_outstanding_amount += order.outstanding_amount;         
        total_allocated_amount += (order.allocated_amount * order.exchange_rate);     
    });      

    console.log('Total Outstanding Amount:', total_outstanding_amount);     
    console.log('Total Allocated Amount:', total_allocated_amount);      

    let outstanding_amount_value = total_outstanding_amount * custom_transaction_exchange_rate;     
    let allocated_amount_value = total_allocated_amount;      

    console.log('Outstanding Amount Value:', outstanding_amount_value);     
    console.log('Allocated Amount Value:', allocated_amount_value);      

    let final_value = (outstanding_amount_value + allocated_amount_value) / total_amount;
    final_value = Math.round(final_value * 100) / 100;
    
    console.log('Final Value:', final_value);
    return final_value;
}

