function calculateTotal(frm, cdt, cdn) {
    var child_doc = locals[cdt][cdn];
    var total_net_weight = 0;
    if (child_doc.custom_quantity_of_cartons) {
        total_net_weight = child_doc.custom_quantity_of_cartons * child_doc.custom_nwkgctn ;
    }
    if (child_doc.custom_nwkgctn) {
        total_net_weight = child_doc.custom_nwkgctn * child_doc.custom_quantity_of_cartons ;
    }
    frappe.model.set_value(cdt, cdn, 'custom_total_net_weight', total_net_weight);
}

function calculatetotal_gw(frm, cdt, cdn) {
    var child_doc = locals[cdt][cdn];
    var total_net_weight = 0;
    if (child_doc.custom_quantity_of_cartons) {
        total_net_weight = child_doc.custom_quantity_of_cartons * child_doc.custom_gwkgctn ;
    }
    if (child_doc.custom_gwkgctn) {
        total_net_weight = child_doc.custom_gwkgctn * child_doc.custom_quantity_of_cartons ;
    }
    frappe.model.set_value(cdt, cdn, 'custom_total_gross_weight', total_net_weight);
}

function calculate_per_item_net_weight(frm, cdt, cdn, total_net_weight) {
    var child_doc = locals[cdt][cdn];
    var per_item_net_weight = 0;

    if (child_doc.custom_total_net_weight > 0 && child_doc.qty > 0) {
        per_item_net_weight = child_doc.custom_total_net_weight / child_doc.qty;
    } else {
        per_item_net_weight = 0;
    }

    frappe.model.set_value(cdt, cdn, 'net_weight', per_item_net_weight);
}

function total_gw_calc(frm) {
    var total_gross_weight = 0;

    frm.doc.items.forEach(row => {
        total_gross_weight += row.custom_total_gross_weight;
    }); 


    frm.set_value('gross_weight_pkg', total_gross_weight);
}

