frappe.ui.form.on('Material Request', {
    validate: async function(frm) {
        for (let item of frm.doc.items) {
            if (!item.schedule_date || !item.item_code) continue;

            let res = await frappe.call({
                method: 'frappe.client.get_value',
                args: {
                    doctype: 'Item',
                    filters: { name: item.item_code },
                    fieldname: 'lead_time_days'
                }
            });

            const lead_days = res.message.lead_time_days || 0;
            const today = frappe.datetime.get_today();
            const min_required_date = frappe.datetime.add_days(today, lead_days);

            if (frappe.datetime.str_to_obj(item.schedule_date) < frappe.datetime.str_to_obj(min_required_date)) {
                frappe.throw(`
                    Dòng item <b>${item.item_code}</b>: Required Date (${item.schedule_date}) không hợp lệ.<br>
                    Phải sau tối thiểu <b>${lead_days}</b> ngày kể từ hôm nay (${today}).
                `);
            }
        }
    }
});

