function update_source_warehouses(frm) {
  frm.doc.items.forEach(row => {
    update_source_warehouse(row);
  });
  frm.refresh_field("items");
}

function update_source_warehouse(row) {
  if (!row.item_code) return;

  if (row.item_code.startsWith("SW-") || row.item_code.startsWith("Piping")) {
    row.source_warehouse = "Semi-finished Goods  - BHV";
  } else {
    row.source_warehouse = "Main Store - BHV";
  }
}
