css:
.print-format th {
    background-color: transparent !important;
    border-bottom: 1px solid !important;
    border-top: none !important;
}
.print-format .ql-editor {
    padding-left: 0px;
    padding-right: 0px;
}

.print-format table {
    margin-bottom: 0px;
    }
.print-format .table-data tr:last-child { 
    border-bottom: 1px solid !important;
}

.print-format .table-inner tr:last-child {
    border-bottom:none !important;
}
.print-format .table-inner {
    margin: 0px 0px;
}

.print-format .table-data ul li { 
    color:#787878 !important;
}

.no-top-border {
    border-top:none !important;
}

.table-inner td {
    padding-left: 0px !important;    
    padding-top: 1px !important;
    padding-bottom: 1px !important;
    color:#787878 !important;
}

.total {
    background-color: lightgrey !important;
    padding-top: 4px !important;
    padding-bottom: 4px !important;
}

.qty-col {
  width: 60px;
  max-width: 60px;
  text-align: right;   /* s s thng hng bn phi */
}

.uom-col {
  width: 50px;
  max-width: 50px;
  text-align: center;  /* ch UOM cn gia */
}

.number {
  width: 60px;
  max-width: 60px;
  text-align: right;  /* ch No cn gia */
}

.letter-head {
  margin-bottom: 5px; /* thu nh khong cch */
  max-height: 60px;
}

.letter-head img {
  margin-top: -10px; /* nu header vn xa th ko ln */
}


.signature-table {
  width: 100%;
  margin-top: 20px;
  border-collapse: collapse;
}

.signature-table td {
  width: 33%;
  text-align: center;
  padding: 5px;
}

/* Darker table borders */
.print-format table,
.print-format table th,
.print-format table td {
  border: 1px solid #000 !important;
  border-collapse: collapse !important;
}

.print-format table th {
  background-color: #f2f2f2;
  font-weight: bold;
}

/* Reduce space between sections */
.print-format .section-break {
  margin-top: 2px !important;
  margin-bottom: 2px !important;
  padding: 0 !important;
}

/* Reduce spacing between labels and values */
.print-format .label,
.print-format .value {
  margin: 0 !important;
  padding: 2px 0 !important;
  line-height: 1 !important;
}

/* Remove big gap from DRAFT status */
div[document-status="draft"] {
  position: absolute !important;   /* overlay instead of block */
  top: 10px !important;
  right: 20px !important;
  text-align: right !important;
  width: auto !important;
  margin: 0 !important;
  padding: 0 !important;
}

/* Style the DRAFT text itself */
div[document-status="draft"] h4 {
  margin: 0 !important;
  padding: 0 !important;
  font-size: 14px !important;  /* smaller */
  line-height: 1 !important;
}


/* Hide the default document heading */
.print-heading {
  display: none !important;
  height: 0 !important;
  margin: 0 !important;
  padding: 0 !important;
  overflow: hidden !important;
}

/* Target the cutting material table */
div[data-fieldname="cutting_material"] table {
    font-size: 11px; /* Change this to your preferred size */
}

/* Optional: make table headers slightly bigger */
div[data-fieldname="cutting_material"] table th {
    font-size: 12px; /* Slightly bigger for headers */
}

/* Optional: adjust the div.value inside each cell */
div[data-fieldname="cutting_material"] table td .value {
    font-size: 11px; /* Matches table font size */

}

.col-xs-12.column-break {
    margin-top: 7px; /* adjust as needed */
}

/* Reduce spacing for the header */
.print-heading-section h2 {
    margin: 0;           /* remove top & bottom margin */
    padding: 0;          /* remove any padding if present */
    font-size: 18px;     /* adjust font size if needed */
    line-height: 1.2;    /* make lines closer */
}

/* Reduce spacing for the sub-heading */
.print-heading-section h2 .sub-heading {
    display: block;      /* keep it below the main title */
    margin: 2px 0 0 0;   /* small top margin, no bottom margin */
    font-size: 12px;     /* smaller font size */
}

/* Reduce spacing for the date fields container */
.col-xs-6.column-break {
    margin-top: 0;       /* reduce top margin */
    padding-top: 0;      /* optional */
}

/* Reduce spacing for each row */
.row.data-field {
    margin-top: 1px;     /* small spacing between rows */
    margin-bottom: 1px;  /* small spacing between rows */
    line-height: 1;    /* tighter line spacing */
}

/* Reduce spacing for all rows in column-break sections */
.row.data-field {
    margin-top: 2px !important;   /* tiny spacing above */
    margin-bottom: 2px !important; /* tiny spacing below */
    line-height: 1 !important;    /* tighter line spacing */
    padding: 0 !important;        /* remove internal padding */
}

/* Reduce spacing inside labels and value divs */
.row.data-field label,
.row.data-field .value {
    margin: 0 !important;
    padding: 0 !important;
    line-height: 0.5 !important;
}

/* Optional: adjust column padding */
.col-xs-5, .col-xs-7 {
    padding-top: 0 !important;
    padding-bottom: 0 !important;
}


body, body * {
    color: black !important;
}

/* Force table cell padding and line-height */
div[data-fieldname="cutting_material"] table th,
div[data-fieldname="cutting_material"] table td,
div[data-fieldname="cutting_material"] table td .value {
    padding: 2px 3px !important;   /* small padding */
    margin: 0 !important;          /* remove extra margin */
    line-height: 1 !important;     /* tighter vertical spacing */
}

/* Optional: slightly smaller font for compactness */
div[data-fieldname="cutting_material"] table td .value {
    font-size: 11px !important;
}

